import { useState, useEffect } from "react";
import { ethers } from 'ethers';
import { Row, Col, Card, Button } from 'react-bootstrap';

const Home = ({marketplace, nft}) =>{

    const [loading, setLoading] = useState(true);
    const [items, setItems] = useState([]);

    const loadMarketPlaceItems = async () => {

        const itemCount = await marketplace.itemCount();
        console.log('itemCount', itemCount)
        let items = [];

        for (let i = 1; i<= itemCount; i++){
            const item = await marketplace.items(i);
            if(!item.sold) {
                const uri = await nft.tokenURI(item.tokenId);
                const response = await fetch(uri);
                const metadata = await response.json();
                const totalPrice = await marketplace.getTotalPrice(item.itemId);
                items.push({
                    totalPrice,
                    itemId: item.itemId,
                    seller: item.seller,
                    name: metadata.name,
                    description: metadata.description,
                    image: metadata.image
                })
            }
        }

        setLoading(false)
        setItems(items);
    }

    const buyMarketItem = async (item) => {
        await (await marketplace.purchaseItem(item.itemId, {value: item.totalPrice})).wait() //un await que lleva dentro una llamada al contrato y el .wait para asegurarse que se ejecute

        loadMarketPlaceItems()
    }

    useEffect(() => {
        loadMarketPlaceItems()
    }, [])

    if(loading) return (
        <main style={{padding: '1rem 0'}}>
            <h2>Loading...</h2>
        </main>
    )

    return (
        <div className="flex justify-center">
            {items.length > 0 ? 
                <div className="px-5 container">
                    <Row xs={1} md={2} lg={4} className="g-4 py-5">
                        {items.map((item, idx) => (
                            <Col key={idx} className="overflow-hidden">
                                <Card>
                                    <Card.Img variant="top" src={item.image} />
                                    <Card.Body color="secondary">
                                        <Card.Title>{item.name}</Card.Title>
                                        <Card.Text>{item.description}</Card.Text>
                                    </Card.Body>
                                    <Card.footer>
                                        <div className="d-grid">
                                            <button onClick={() => buyMarketItem()(item)} variant="primary" size="large"> Buy by {ethers.utils.formatEther(item.totalPrice)} ETH</button>
                                        </div>
                                    </Card.footer>
                                </Card>
                            </Col>
                        ))}
                    </Row>
                </div>
                : (
                    <main style={{padding:"1rem 0"}}>
                        <h2>No Assets</h2>
                    </main>
                )}
        </div>
    )
}

export default Home;
