import { useState, useEffect } from 'react';
import { ethers } from 'ethers';
import { Row, Col, Card } from 'react-bootstrap';

function renderSoldItems(items){
    return (
        <>
            <h2>Sold</h2>
            <Row xs={1} md={2} lg={4} className="g-4 py-3">
                {items.map((item, idx) => {
                    <Col key={idx} className='overflow-hidden'>
                        <Card>
                            <Card.Img variant="top" src={item.image}/>
                            <Card.Footer>
                                For {ethers.utils.formatEther(item.totalPrice)} ETH - Received {ethers.utils.formatEther(item.price)}
                            </Card.Footer>
                        </Card>
                    </Col>
                })}
            </Row>
        </>
    )
}

export default function MylistedItems({marketplace, nft, account}) {
    const [loading, setLoading] = useState(true);
    const [listedItems, setListedItems] = useState([]);
    const [soldItems, setSoldItems] = useState([]);

    const loadListedItems = async() => {
        const itemCount = marketplace.itemCount();
        let listedItems = [];
        let soldItems = [];
        for (let indx = 1; indx <= itemCount; indx++){
            const i = await marketplace.items(indx);
            if(i.seller.toLowercase() === account){ //para saber si el nft ha sido vendido por nosotros (lo comparamos con el account)
                const uri = await nft.tokenUri(i.tokenId);
                const response = await fetch(uri);
                const metadata = await response.json();
                const totalPrice = await marketplace.getTotalPrice(i.itemId);
                let item = {
                    totalPrice,
                    price: i.price,
                    itemId: i.itemId,
                    name: metadata.name,
                    description: metadata.description,
                    image: metadata.image
                };

                listedItems.push(item);
                if(i.sold) soldItems.push(item);

            }
        }

        setLoading(false);
        setListedItems(listedItems);
        setSoldItems(soldItems);

    }

    useEffect(() =>{
        loadListedItems();

    }, []);

    if(loading) return (
        <main style={{ padding: "1ren 0"}}>
            <h2>Loading ...</h2>
        </main>
    )

    return (
        <div className='flex justify-center'>
            {listedItems.length > 0 ? 
                <div className='px-5 py-3 container'>
                    <Row xs={1} md={2} lg={4} className="g-4 py-3">
                        {listedItems.map((item, idx) => {
                            <Col key={idx} className='overflow-hidden'>
                                <Card>
                                    <Card.Img variant="top" src={item.image}/>
                                    <Card.Footer>
                                        {ethers.utils.formatEther(item.totalPrice)} ETH
                                    </Card.Footer>
                                </Card>
                            </Col>
                        })}
                    </Row>

                    {soldItems.length > 0 && renderSoldItems(soldItems)} 

                </div>
            : (
                <main style={{ padding: "1rem 0"}}>
                    <h2>No Assets</h2>
                </main>

            )}
        </div>
    )
}
