import { BrowserRouter, Routes, Route} from 'react-router-dom';
import Navigation from './NavBar';
import Home from './Home';
import Create from './Create';
import MyListedItems from './MyListedItems';
import MyPurchases from './MyPurchases';

import MarketPlaceAbi from '../contractsData/Marketplace.json';
import MarketPlaceAddress from '../contractsData/Marketplace-address.json';
import NFTAbi from '../contractsData/NFT.json';
import NFTAddress from '../contractsData/NFT-address.json';
import { ethers } from 'ethers';
import { useState } from 'react';
import { Spinner } from 'react-bootstrap';

import './App.css';


function App() {

  const [loading, setLoading] = useState(true);
  const [account, setAccount] = useState(null);
  const [nft, setNFT ] = useState({});
  const [marketplace, setMarketplace] = useState({});

  const web3Handler = async() =>{ //Para ver si esta conectado metamask

    const accounts = await window.ethereum.request({method: 'eth_requestAccounts'});
    console.log(accounts)
    setAccount(accounts[0]);

    const provider = new ethers.providers.Web3Provider(window.ethereum);
    const signer = provider.getSigner();

    window.ethereum.on('chainChanged', (chainId) =>{ //recarga la pagina comprobando el chainId
      window.location.reload();
    });

    window.ethereum.on('accountsChanged', async(accounts) =>{ //chequeamos si las cuentas han cambiado
      setAccount(accounts[0]);

      await web3Handler()
    });

    loadContracts(signer);
  }


  const loadContracts = async (signer) => {
    const mktplace = new ethers.Contract(MarketPlaceAddress.address, MarketPlaceAbi.abi, signer);
    setMarketplace(mktplace);

    const nft = new ethers.Contract(NFTAddress.address, NFTAbi.abi, signer);
    setNFT(nft);

    setLoading(false);

  }

  return (
    <BrowserRouter>
      <div className='App'>
        <>
          <Navigation web3Handler={web3Handler} account={account}/>
        </>

        <div>
          {loading ? (
            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '80vh'}}> 
              <Spinner animation='border' style={{display: 'flex'}}/>
              <p className='mx-3 my-0'>Waiting for Metamask</p>
            </div>) : (
              <Routes>
                <Route path="/" element={ <Home marketplace={marketplace} nft={nft}/> } />
                <Route path="/create" element={ <Create marketplace={marketplace} nft={nft}/> } />
                <Route path="/my-listed-items" element={ <MyListedItems marketplace={marketplace} nft={nft}/> } />
                <Route path="/my-purchases" element={ <MyPurchases marketplace={marketplace} nft={nft}/> } />
              </Routes>
            )
          }
        </div>
      </div>
    </BrowserRouter>
  );
}

export default App;
